import React, { Component } from 'react';

let imageArray = [];

let currentGameArray = [];

let currentScore = 0;
let highScore = 0;

const styles = {
    container:{
        paddingTop: "50px",
        paddingBottom: "50px",
        paddingLeft: "0px",
        paddingRight: "0px",
        display: "flex",
        flexWrap: "wrap",
        margin: "0 auto",
        justifyContent: "center",
    }
};

class Game extends Component {

    randomImage = () =>{
        // console.log(imageArray);
        let randomNumber = (Math.floor(Math.random() * 49)+1);
        if(imageArray.includes(randomNumber)){
            return this.randomImage();
        }
        else{
            imageArray.push(randomNumber);
            return "/react-clicky-game/us-flags/"+randomNumber+".png";
        }
    }
    changeImages = () =>{
        const styles = {
            images: {
                height: "150px",
                width: "150px",
                margin: "8px 8px",
                padding: "0px",
                display: "block",
                backgroundImage: 'url(' + this.randomImage() + ')',
                backgroundSize: "contain",
                backgroundRepeat: "no-repeat",
                backgroundPosition: "center",
            }
        };
        return styles.images;
    }

    imageClicked = (e) =>{
        let backgroundURL = e.target.style.backgroundImage;
        if(currentGameArray.includes(backgroundURL)){
            //reset score, clear array, etc
            currentScore=0;
            // highScore=0;
            currentGameArray=[];
            imageArray = [];
            document.getElementById("game-scores").innerHTML = "Score: "+currentScore+" | Top Score: "+highScore;
            this.forceUpdate();
        }
        else{
            currentGameArray.push(backgroundURL);
            currentScore++;
            if(currentScore>=highScore){
                highScore=currentScore;
            }
            document.getElementById("game-scores").innerHTML = "Score: "+currentScore+" | Top Score: "+highScore;
        }
        this.shuffleBoard(document.getElementsByClassName("click-item"));
    };

    shuffleArray = (array) => {
        var newArray = Array.prototype.slice.call( array )
        for (var i = newArray.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = newArray[i];
            newArray[i] = newArray[j];
            newArray[j] = temp;
        }
        return newArray;
    }

    shuffleBoard = (elems) => {

        var divs = elems;
        let newDivs = this.shuffleArray(divs);
        for(let i = 0; i<newDivs.length; i++){
            if(i<4){
                document.getElementById("row1").appendChild(newDivs[i]);
            }
            else if(i<8){
                document.getElementById("row2").appendChild(newDivs[i]);
            }
            else{
                document.getElementById("row3").appendChild(newDivs[i]);
            }
        }
    }

    getParsedDate(strDate){
        var strSplitDate = String(strDate).split(' ');
        var date = new Date(strSplitDate[0]);
        // alert(date);
        var dd = date.getDate();
        var mm = date.getMonth() + 1; //January is 0!

        var yyyy = date.getFullYear();
        if (dd < 10) {
            dd = '0' + dd;
        }
        if (mm < 10) {
            mm = '0' + mm;
        }
        date =  dd + "-" + mm + "-" + yyyy;
        return date.toString();
    }

    render() {
        return (
            <main className="container" id="gameBoard" style={styles.container}>
                <div className="row" id="row1">
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                </div>
                <div className="row" id="row2">
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                </div>
                <div className="row" id="row3">
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                    <div role="img" aria-label="click item" className="click-item .col-*" style={this.changeImages()} onClick={this.imageClicked}>
                    </div>
                </div>
                <div>
                    <h1>{this.getParsedDate('1976-04-19T12:59-0500')}</h1>
                </div>
            </main>
        );
    };
}

export default Game;
