import React from 'react';

let imageArray = [];

let currentGameArray = [];

let currentScore = 0;
let highScore = 0;

const randomImage = () =>{
    console.log(imageArray);
    let randomNumber = (Math.floor(Math.random() * 49)+1);
    if(imageArray.includes(randomNumber)){
        return randomImage();
    }
    else{
        imageArray.push(randomNumber);
        return "/us-flags/"+randomNumber+".png";
    }
}

const styles = {
    container:{
        paddingTop: "50px",
        paddingBottom: "50px",
        display: "flex",
        flexWrap: "wrap",
        margin: "0 auto",
        justifyContent: "center",
    }
};

const changeImages = () =>{
    const styles = {
        images: {
            height: "150px",
            width: "150px",
            margin: "8px 25px",
            display: "block",
            backgroundImage: 'url(' + randomImage() + ')',
            backgroundSize: "contain",
            backgroundRepeat: "no-repeat",
            backgroundPosition: "center",
        }
    };
    console.log(styles);
    return styles.images;
}

const imageClicked = (e) =>{
    console.log(e.target.style.backgroundImage);
    console.log(currentGameArray);
    console.log(currentScore);
    console.log(highScore);
    let backgroundURL = e.target.style.backgroundImage;
    if(currentGameArray.includes(backgroundURL)){
        //reset score, clear array, etc
        currentScore=0;
        // highScore=0;
        currentGameArray=[];
        document.getElementById("game-scores").innerHTML = "Score: "+currentScore+" | Top Score: "+highScore;
    }
    else{
        currentGameArray.push(backgroundURL);
        currentScore++;
        if(currentScore>=highScore){
            highScore=currentScore;
        }
        document.getElementById("game-scores").innerHTML = "Score: "+currentScore+" | Top Score: "+highScore;
    }
    shuffleBoard(document.getElementsByClassName("click-item"));
};

const shuffleBoard = (elems) => {
    var parent = document.getElementById("gameBoard");
    var divs = parent.children;
    var frag = document.createDocumentFragment();
    while (divs.length) {
        frag.appendChild(divs[Math.floor(Math.random() * divs.length)]);
    }
    parent.appendChild(frag);

}


function Game (){
    changeImages();
    return(
        <main className="container" id="gameBoard" style={styles.container}>
            <div className="row">
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
            </div>
            <div className="row">
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
            </div>
            <div className="row">
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
                <div role="img" aria-label="click item" className="click-item" style={changeImages()} onClick={imageClicked}>
                </div>
            </div>
        </main>
    );
}

export default Game;
