import React, { Component } from 'react';

const styles = {
    container:{
        paddingTop: "50px",
        paddingBottom: "50px",
        paddingLeft: "0px",
        paddingRight: "0px",
        display: "flex",
        flexWrap: "wrap",
        margin: "0 auto",
        justifyContent: "center",
    }
};
const [isShown, setIsShown] = useState(false);

class Game extends Component {

    render() {
        return (
            <main className="container" id="gameBoard" style={styles.container}>
                <div className="button-div">
                    <button type="button" class="btn btn-primary" style={{margin: "20px"}}
                    onMouseEnter={() => setIsShown(true)}
                    onMouseLeave={() => setIsShown(false)}
                    >Yes</button>
                    {isShown && (
                        <div>
                        I'll appear when you hover over the button.
                        </div>
                    )}
                    <button type="button" class="btn btn-primary" style={{margin: "20px"}}>No</button>
                </div>
            </main>
        );
    };
}

export default Game;
