import React from 'react';

let imageArray = [];

let currentGameArray = [];

let currentScore = 0;
let highScore = 0;

const randomImage = () =>{
    // console.log(imageArray);
    let randomNumber = (Math.floor(Math.random() * 49)+1);
    if(imageArray.includes(randomNumber)){
        return randomImage();
    }
    else{
        imageArray.push(randomNumber);
        return "/us-flags/"+randomNumber+".png";
    }
}

const styles = {
    container:{
        paddingTop: "50px",
        paddingBottom: "50px",
        display: "flex",
        flexWrap: "wrap",
        margin: "0 auto",
        justifyContent: "center",
    }
};

const changeImages = () =>{
    const styles = {
        images: {
            height: "150px",
            width: "150px",
            margin: "8px 25px",
            display: "block",
            backgroundImage: 'url(' + randomImage() + ')',
            backgroundSize: "contain",
            backgroundRepeat: "no-repeat",
            backgroundPosition: "center",
        }
    };
    // conssole.log(styles);
    return styles.images;
}

const imageClicked = (e) =>{
    // console.log(e.target.style.backgroundImage);
    // console.log(currentGameArray);
    // console.log(currentScore);
    // console.log(highScore);
    let backgroundURL = e.target.style.backgroundImage;
    if(currentGameArray.includes(backgroundURL)){
        //reset score, clear array, etc
        currentScore=0;
        // highScore=0;
        currentGameArray=[];
        document.getElementById("game-scores").innerHTML = "Score: "+currentScore+" | Top Score: "+highScore;
        changeImages();
    }
    else{
        currentGameArray.push(backgroundURL);
        currentScore++;
        if(currentScore>=highScore){
            highScore=currentScore;
        }
        document.getElementById("game-scores").innerHTML = "Score: "+currentScore+" | Top Score: "+highScore;
    }
    shuffleBoard(document.getElementsByClassName("click-item"));
};

const shuffleArray = (array) => {
        var currentIndex = array.length, temporaryValue, randomIndex;
        var newArray = [];
        // While there remain elements to shuffle...
        while (0 !== currentIndex) {

          // Pick a remaining element...
          randomIndex = Math.floor(Math.random() * currentIndex);
          currentIndex -= 1;

          // And swap it with the current element.
          temporaryValue = array[currentIndex];
          newArray[currentIndex] = array[randomIndex];
          newArray[randomIndex] = temporaryValue;
        }
        return newArray;
}

